('',	213,	2,	'220120901',	'Ramadhani',	'Penerimaan Mahasiswa Baru Tahun Ajaran 2019/2020',	'',	'penerimaan-mahasiswa-baru-tahun-ajaran-20192020',	'Y',	'<p><img title=\"Penerimaan Mahasiswa Baru Tahun Ajaran 2019/2020\" src=\"tinymcpuk2/tinymce/plugins/imagemanager/dosen/untuk_web_2.jpg\" alt=\"\" width=\"778\" height=\"454\" /></p>',	'',	'Berita',	'Rabu',	'2018-12-05',	'06:59:13',	'181205065913untuk web (2).jpg',	'',	'0000-00-00',	'N',	'6',	'181205065913untuk web (2).jpg',	'N',	'N',	'N',	'N',	213),
